// Core variables
$version: "0.5.8";

// Core features
$rtl: false;

// Core colors
$primary-color: #0300ff;
$primary-color-dark: darken($primary-color, 3%);
$primary-color-light: lighten($primary-color, 3%);
$secondary-color: lighten($primary-color, 37.5%);
