// Avatars
.avatar {
  @include avatar-base();
  background: $primary-color;
  border-radius: 50%;
  color: rgba($light-color, .85);
  display: inline-block;
  font-weight: 300;
  line-height: 1.25;
  margin: 0;
  position: relative;
  vertical-align: middle;

  &.avatar-xs {
    @include avatar-base($unit-4);
  }
  &.avatar-sm {
    @include avatar-base($unit-6);
  }
  &.avatar-lg {
    @include avatar-base($unit-12);
  }
  &.avatar-xl {
    @include avatar-base($unit-16);
  }

  img {
    border-radius: 50%;
    height: 100%;
    position: relative;
    width: 100%;
    z-index: $zindex-0;
  }

  .avatar-icon,
  .avatar-presence {
    background: $bg-color-light;
    bottom: 14.64%;
    height: 50%;
    padding: $border-width-lg;
    position: absolute;
    right: 14.64%;
    transform: translate(50%, 50%);
    width: 50%;
    z-index: $zindex-0 + 1;
  }

  .avatar-presence {
    background: $gray-color;
    box-shadow: 0 0 0 $border-width-lg $light-color;
    border-radius: 50%;
    height: .5em;
    width: .5em;

    &.online {
      background: $success-color;
    }

    &.busy {
      background: $error-color;
    }

    &.away {
      background: $warning-color;
    }
  }

  &[data-initial]::before {
    color: currentColor;
    content: attr(data-initial);
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translate(-50%, -50%);
    z-index: $zindex-0;
  }
}

sources.../node_modules/spectre.css/src/mixins/_:
// Avatar mixin
@mixin avatar-base($size: $unit-8) {
  font-size: $size / 2;
  height: $size;
  width: $size;
}

// Badges
.badge {
  position: relative;
  white-space: nowrap;

  &[data-badge],
  &:not([data-badge]) {
    &::after {
      background: $primary-color;
      background-clip: padding-box;
      border-radius: .5rem;
      box-shadow: 0 0 0 .1rem $bg-color-light;
      color: $light-color;
      content: attr(data-badge);
      display: inline-block;
      transform: translate(-.05rem, -.5rem);
    }
  }
  &[data-badge] {
    &::after {
      font-size: $font-size-sm;
      height: .9rem;
      line-height: 1;
      min-width: .9rem;
      padding: .1rem .2rem;
      text-align: center;
      white-space: nowrap;
    }
  }
  &:not([data-badge]),
  &[data-badge=""] {
    &::after {
      height: 6px;
      min-width: 6px;
      padding: 0;
      width: 6px;
    }
  }

  // Badges for Buttons
  &.btn {
    &::after {
      position: absolute;
      top: 0;
      right: 0;
      transform: translate(50%, -50%);
    }
  }

  // Badges for Avatars
  &.avatar {
    &::after {
      position: absolute;
      top: 14.64%;
      right: 14.64%;
      transform: translate(50%, -50%);
      z-index: $zindex-1;
    }
  }
}

